<style>
    header {
        gap: var(--gap8);
        justify-content: space-between;
    }

    header > * {
        align-items: center;
        display: flex;
        flex-flow: row nowrap;
        gap: var(--gap8);
    }

    a.home > h1 {
        color: var(--white);
    }

    @media (width < 26.5rem) {
        a.home > h1.no_wrap {
            display: none;
        }
    }

    @media (26.5rem <= width) {
        a.home > h1.wrap {
            display: none;
        }
    }

    @media (width < 30rem) {
        a.home > h1 {
            font-size: 1.5rem;
        }
    }

    @media (30rem <= width) {
        a.home > h1 {
            font-size: 1.75rem;
        }
    }

    a > svg {
        fill: var(--white);
        height: 2.5rem;
        width: 2.5rem;
    }
</style>
